import "./intro.scss"
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';
import {init} from 'ityped';

export default function Intro() {

    const text= useRef();

    useEffect(() => {
        init(text.current, {
            showCursor: false,
            strings: ["Engenharia", "de", "Informática"],
        })
    }, [input])
    return (
        <div className="intro" id="intro">

            <div className="left">
                <div className="img">
                    <img src="assets/woman.png"></img>
                </div>
            </div>

            <div className="right">
                <div className="wrapper">
                    <h1>Thaís Menezes</h1>
                    <h2>Finalista em <span ref={text}></span></h2>
                    <h3>Estágio curricular foi em desenvolvimento Web, onde tive a possibilidade de iniciar na parte do front-end.</h3>
                </div>
                <a href="#portfolio">
                    <ArrowDownwardIcon className="down"></ArrowDownwardIcon>
                </a>
            </div>
        </div>
    )
}
